document.addEventListener("DOMContentLoaded", function () {
    let chatbotBox = null;
    let isChatboxMaximized = false;
    const defaultChatboxWidth = "400px";
    const defaultChatboxHeight = "500px";
    const storedWidth = localStorage.getItem("chatboxWidth");
    const storedHeight = localStorage.getItem("chatboxHeight");
    const isChatOpen = localStorage.getItem("chatboxOpen") === "true";

    // *** ALTERAÃ‡ÃƒO CRÃTICA AQUI: SELECIONAMOS O BOTÃƒO EXISTENTE DO XML ***
    // NÃ£o criamos um novo botÃ£o. Selecionamos o que jÃ¡ foi injetado pelo Odoo.
    const chatbotButton = document.getElementById("chatbot-toggle-button");

    // Verificamos se o botÃ£o existe antes de adicionar eventos ou manipular.
    if (chatbotButton) {
        // Anexa o evento de clique ao botÃ£o existente.
        chatbotButton.addEventListener("click", function () {
            chatbotButton.style.display = "none"; // Esconde o botÃ£o ao abrir o chat.
            if (!chatbotBox) {
                createChatbox(); // Cria o chatbox se ainda nÃ£o existir.
            }
            chatbotBox.style.display = "flex"; // Mostra o chatbox.
            localStorage.setItem("chatboxOpen", "true"); // Armazena o estado.
        });

        // LÃ³gica para determinar a visibilidade inicial do botÃ£o e do chatbox.
        if (isChatOpen) {
            createChatbox(); // Cria o chatbox se o estado for 'aberto'.
            chatbotButton.style.display = "none"; // Oculta o botÃ£o se o chat estiver aberto.
        } else {
            chatbotButton.style.display = "block"; // Mostra o botÃ£o se o chat estiver fechado.
        }

    } else {
        console.error("O botÃ£o do chatbot com ID 'chatbot-toggle-button' nÃ£o foi encontrado no DOM!");
    }

    // FunÃ§Ã£o para criar o chatbox dinamicamente.
    function createChatbox() {
        chatbotBox = document.createElement("div");
        chatbotBox.id = "chatbot-box";
        chatbotBox.style.cssText = `
            display: ${isChatOpen ? 'flex' : 'none'};
            position: fixed;
            bottom: 90px;
            right: 20px;
            width: ${storedWidth || defaultChatboxWidth};
            height: ${storedHeight || defaultChatboxHeight};
            background-color: white;
            border: 1px solid #e0e0e0;
            border-radius: 10px;
            box-shadow: 0 8px 25px rgba(0,0,0,0.2);
            z-index: 9999;
            flex-direction: column;
            overflow: hidden;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            transition: all 0.3s ease;
            resize: both;
            min-width: 300px;
            min-height: 350px;
        `;

        // CriaÃ§Ã£o do cabeÃ§alho do chatbox.
        const header = document.createElement("div");
        header.style.cssText = `
            padding: 10px;
            display: flex;
            justify-content: space-between;
            align-items: center;
            background-color: #006400; /* Verde Escuro */
            color: white;
            border-top-left-radius: 10px;
            border-top-right-radius: 10px;
            cursor: move;
            position: relative;
        `;

        // Indicador de redimensionamento no canto superior esquerdo (visual).
        const resizeGrip = document.createElement("div");
        resizeGrip.style.cssText = `
            position: absolute;
            top: 2px;
            left: 4px;
            width: 10px;
            height: 10px;
            background-color: white;
            border-radius: 50%;
            opacity: 0.5;
            cursor: nwse-resize;
        `;
        header.appendChild(resizeGrip);

        // TÃ­tulo do chatbox.
        const title = document.createElement("span");
        title.innerText = "AI Chat";
        title.style.fontWeight = "bold";

        // BotÃµes de maximizar e fechar.
        const buttons = document.createElement("div");

        const maximizeButton = document.createElement("button");
        maximizeButton.id = "maximize-button";
        maximizeButton.innerHTML = "&#x26F6;"; // Ãcone de maximizar
        maximizeButton.style.cssText = `
            margin-right: 10px;
            background: none;
            border: none;
            color: white;
            font-size: 18px;
            cursor: pointer;
        `;

        const closeButton = document.createElement("button");
        closeButton.id = "close-button";
        closeButton.innerHTML = "&times;"; // Ãcone de fechar (X)
        closeButton.style.cssText = `
            background: none;
            border: none;
            color: white;
            font-size: 18px;
            cursor: pointer;
        `;

        buttons.appendChild(maximizeButton);
        buttons.appendChild(closeButton);
        header.appendChild(title);
        header.appendChild(buttons);
        chatbotBox.appendChild(header);

        // O iframe que carregarÃ¡ o conteÃºdo do chatbot (Ex: OpenAI).
        const iframe = document.createElement("iframe");
        // ATENÃ‡ÃƒO: Carregar chat.openai.com diretamente pode ter restriÃ§Ãµes de seguranÃ§a (X-Frame-Options).
        // Para integraÃ§Ã£o real, vocÃª precisaria de uma API ou serviÃ§o que permita embed.
        iframe.src = "https://chat.openai.com"; 
        iframe.style.cssText = `
            width: 100%;
            height: 100%;
            border: none;
            flex: 1;
        `;
        chatbotBox.appendChild(iframe);
        document.body.appendChild(chatbotBox);

        // Anexa os listeners para os botÃµes do chatbox.
        maximizeButton.addEventListener("click", toggleChatboxSize);
        closeButton.addEventListener("click", function () {
            chatbotBox.style.display = "none"; // Esconde o chatbox.
            chatbotButton.style.display = "block"; // Mostra o botÃ£o original do XML.
            localStorage.setItem("chatboxOpen", "false"); // Armazena o estado como 'fechado'.
        });

        // Observador de redimensionamento para persistir o tamanho.
        new ResizeObserver(() => {
            localStorage.setItem("chatboxWidth", chatbotBox.style.width);
            localStorage.setItem("chatboxHeight", chatbotBox.style.height);
        }).observe(chatbotBox);
    }

    // FunÃ§Ã£o para alternar entre o estado maximizado/normal do chatbox.
    function toggleChatboxSize() {
        if (isChatboxMaximized) {
            chatbotBox.style.width = defaultChatboxWidth;
            chatbotBox.style.height = defaultChatboxHeight;
            chatbotBox.style.right = "20px";
            chatbotBox.style.bottom = "90px";
            chatbotBox.style.left = "auto";
            chatbotBox.style.top = "auto";
            chatbotBox.style.resize = "both";
            document.getElementById("maximize-button").innerHTML = "&#x26F6;"; // Ãcone de restaurar
        } else {
            chatbotBox.style.width = "calc(100% - 40px)";
            chatbotBox.style.height = "calc(100% - 110px)";
            chatbotBox.style.right = "20px";
            chatbotBox.style.bottom = "90px";
            chatbotBox.style.left = "20px";
            chatbotBox.style.top = "20px";
            chatbotBox.style.resize = "none";
            document.getElementById("maximize-button").innerHTML = "&#x25A1;"; // Ãcone de maximizar
        }
        isChatboxMaximized = !isChatboxMaximized;
    }
});
