document.addEventListener("DOMContentLoaded", function () {
    let chatbotBox = null;
    let isChatboxMaximized = false;
    const defaultChatboxWidth = "400px";
    const defaultChatboxHeight = "500px";
    const storedWidth = localStorage.getItem("chatboxWidth");
    const storedHeight = localStorage.getItem("chatboxHeight");
    const isChatOpen = localStorage.getItem("chatboxOpen") === "true";

    // ****** ALTERA칂츾O AQUI: Selecionar o bot칚o existente ******
    const chatbotButton = document.getElementById("chatbot-toggle-button"); 
    // Remova todo o c칩digo que cria um novo 'div' para o bot칚o, seus estilos e o 'appendChild'
    // chatbotButton.innerText = "游눫"; // N칚o precisa
    // chatbotButton.id = "chatbot-launcher-button"; // N칚o precisa
    // chatbotButton.style.cssText = `...`; // O estilo j치 est치 no XML, mas pode ser ajustado aqui se quiser
    // document.body.appendChild(chatbotButton); // N칚o precisa

    // Certifique-se de que o bot칚o foi encontrado (boa pr치tica)
    if (chatbotButton) {
        chatbotButton.addEventListener("click", function () {
            chatbotButton.style.display = "none";
            if (!chatbotBox) {
                createChatbox();
            }
            chatbotBox.style.display = "flex";
            localStorage.setItem("chatboxOpen", "true");
        });

        // Auto-cria칞칚o se estiver aberto
        if (isChatOpen) {
            createChatbox();
            // Se o chat estiver aberto, o bot칚o deve estar oculto
            chatbotButton.style.display = "none"; 
        } else {
            // Se o chat N츾O estiver aberto, o bot칚o deve estar vis칤vel
            chatbotButton.style.display = "block";
        }

    } else {
        console.error("Chatbot button not found in the DOM!");
    }
    // **********************************************************


    function createChatbox() {
        chatbotBox = document.createElement("div");
        chatbotBox.id = "chatbot-box";
        chatbotBox.style.cssText = `
            display: ${isChatOpen ? 'flex' : 'none'};
            position: fixed;
            bottom: 90px;
            right: 20px;
            width: ${storedWidth || defaultChatboxWidth};
            height: ${storedHeight || defaultChatboxHeight};
            background-color: white;
            border: 1px solid #e0e0e0;
            border-radius: 10px;
            box-shadow: 0 8px 25px rgba(0,0,0,0.2);
            z-index: 9999;
            flex-direction: column;
            overflow: hidden;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            transition: all 0.3s ease;
            resize: both;
            min-width: 300px;
            min-height: 350px;
        `;

        const header = document.createElement("div");
        // ... (restante do c칩digo do header e bot칫es de maximize/fechar)
        header.style.cssText = `
            padding: 10px;
            display: flex;
            justify-content: space-between;
            align-items: center;
            background-color: #6a0dad; /* Cor alterada para combinar com o bot칚o */
            color: white;
            border-top-left-radius: 10px;
            border-top-right-radius: 10px;
            cursor: move;
            position: relative;
        `;

        const resizeGrip = document.createElement("div");
        resizeGrip.style.cssText = `
            position: absolute;
            top: 2px;
            left: 4px;
            width: 10px;
            height: 10px;
            background-color: white;
            border-radius: 50%;
            opacity: 0.5;
            cursor: nwse-resize;
        `;
        header.appendChild(resizeGrip);

        const title = document.createElement("span");
        title.innerText = "AI Chat";
        title.style.fontWeight = "bold";

        const buttons = document.createElement("div");

        const maximizeButton = document.createElement("button");
        maximizeButton.id = "maximize-button";
        maximizeButton.innerHTML = "&#x26F6;";
        maximizeButton.style.cssText = `
            margin-right: 10px;
            background: none;
            border: none;
            color: white;
            font-size: 18px;
            cursor: pointer;
        `;

        const closeButton = document.createElement("button");
        closeButton.id = "close-button";
        closeButton.innerHTML = "&times;";
        closeButton.style.cssText = `
            background: none;
            border: none;
            color: white;
            font-size: 18px;
            cursor: pointer;
        `;

        buttons.appendChild(maximizeButton);
        buttons.appendChild(closeButton);
        header.appendChild(title);
        header.appendChild(buttons);
        chatbotBox.appendChild(header);

        const iframe = document.createElement("iframe");
        iframe.src = "https://chat.openai.com"; // Considere usar uma URL din칙mica ou do Odoo aqui
        iframe.style.cssText = `
            width: 100%;
            height: 100%;
            border: none;
            flex: 1;
        `;
        chatbotBox.appendChild(iframe);
        document.body.appendChild(chatbotBox);

        // A l칩gica de visibilidade do bot칚o j치 foi movida para fora da fun칞칚o createChatbox
        // e agora controla o bot칚o do XML.

        maximizeButton.addEventListener("click", toggleChatboxSize);
        closeButton.addEventListener("click", function () {
            chatbotBox.style.display = "none";
            chatbotButton.style.display = "block"; // Mostrar o bot칚o do XML
            localStorage.setItem("chatboxOpen", "false");
        });

        // Persist칡ncia do tamanho com ResizeObserver
        new ResizeObserver(() => {
            localStorage.setItem("chatboxWidth", chatbotBox.style.width);
            localStorage.setItem("chatboxHeight", chatbotBox.style.height);
        }).observe(chatbotBox);
    }

    function toggleChatboxSize() {
        if (isChatboxMaximized) {
            chatbotBox.style.width = defaultChatboxWidth;
            chatbotBox.style.height = defaultChatboxHeight;
            chatbotBox.style.right = "20px";
            chatbotBox.style.bottom = "90px";
            chatbotBox.style.left = "auto";
            chatbotBox.style.top = "auto";
            chatbotBox.style.resize = "both";
            document.getElementById("maximize-button").innerHTML = "&#x26F6;";
        } else {
            chatbotBox.style.width = "calc(100% - 40px)";
            chatbotBox.style.height = "calc(100% - 110px)";
            chatbotBox.style.right = "20px";
            chatbotBox.style.bottom = "90px";
            chatbotBox.style.left = "20px";
            chatbotBox.style.top = "20px";
            chatbotBox.style.resize = "none";
            document.getElementById("maximize-button").innerHTML = "&#x25A1;";
        }
        isChatboxMaximized = !isChatboxMaximized;
    }
});
