xpath.script:

console.log("泅 Popup module script started loading.");

document.addEventListener("DOMContentLoaded", function () {
  console.log("笨 DOMContentLoaded fired. Checking popup_shown status.");

  // Remova ou comente a linha abaixo em produﾃｧﾃ｣o. Ela ﾃｩ ﾃｺtil apenas para testes.
  // localStorage.removeItem("popup_shown");

  // Verifica se o usuﾃ｡rio jﾃ｡ viu o popup ou se ﾃｩ um usuﾃ｡rio logado com desconto.
  // Poderﾃｭamos fazer uma chamada ao backend aqui para verificar se o usuﾃ｡rio logado jﾃ｡ tem um cﾃｳdigo.
  if (!localStorage.getItem("popup_shown")) {
    console.log("LocalStorage 'popup_shown' not found. Creating popup.");
    var popup = document.createElement("div");
    popup.id = "main-discount-popup-container";
    popup.innerHTML = `
      <div style="position:fixed;top:20%;left:50%;transform:translateX(-50%);
                   background:#fff;padding:20px;border-radius:8px;box-shadow:0 0 10px rgba(0,0,0,0.5);z-index:9999;">
        <h3>沁 ﾂ｡Obtﾃｩn un 5% de desconto!</h3>
        <p>Introduce tu correo electrﾃｳnico para recibir tu cﾃｳdigo exclusivo.</p>
        <input type="email" id="popup_email" placeholder="Tu correo" style="width: calc(100% - 22px); padding: 8px; margin-bottom: 10px; border: 1px solid #ccc; border-radius: 4px;" />
        <button id="popup_send_email_button" style="background-color: rgb(46, 76, 30); color: white; padding: 10px 15px; border: none; border-radius: 4phoenix-4px; cursor: pointer;">Enviar</button>
        <button id="popup_close_button" style="background-color: #dc3545; color: white; padding: 10px 15px; border: none; border-radius: 4px; cursor: pointer; margin-left: 10px;">X</button>
        <p id="popup_message" style="color: red; margin-top: 10px;"></p>
      </div>
    `;
    document.body.appendChild(popup);
    localStorage.setItem("popup_shown", "true");
    console.log("沍 Popup added to DOM and popup_shown set in localStorage.");

    document.getElementById('popup_send_email_button').addEventListener('click', enviarCorreo);
    document.getElementById('popup_close_button').addEventListener('click', function() {
        document.getElementById('main-discount-popup-container').remove();
        console.log("Popup closed by user.");
    });

  } else {
    console.log("汳｡ Popup not shown. 'popup_shown' already exists in localStorage.");
  }
});

window.enviarCorreo = async function() {
  var emailInput = document.getElementById("popup_email");
  var email = emailInput.value;
  var messageElement = document.getElementById("popup_message");
  messageElement.textContent = ""; // Limpa mensagens anteriores

  if (!email) {
      messageElement.textContent = "Por favor, insira o seu e-mail.";
      emailInput.focus();
      return;
  }

  // Simple email validation
  if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      messageElement.textContent = "Por favor, insira um e-mail vﾃ｡lido.";
      emailInput.focus();
      return;
  }

  console.log("Attempting to send email:", email);
  try {
      const response = await fetch("/newsletter/popup", {
          method: "POST",
          headers: {
              "Content-Type": "application/json",
          },
          body: JSON.stringify({ // ** <-- CORREﾃﾃグ AQUI**
              jsonrpc: "2.0",
              method: "call",
              params: {
                  email: email
              }
          }),
      });

      const data = await response.json(); // Sempre tentar ler a resposta JSON

      if (response.ok) {
          if (data.result && data.result.status === 'ok') { // ** <-- CORREﾃﾃグ AQUI (data.result)**
              alert("ﾂ｡Correo enviado! Revisa tu bandeja.");
              document.getElementById('main-discount-popup-container').remove();
          } else if (data.result && data.result.status === 'exists') { // ** <-- CORREﾃﾃグ AQUI (data.result)**
              alert("Este e-mail jﾃ｡ tem um cﾃｳdigo de desconto registado. Verifique a sua caixa de entrada.");
              document.getElementById('main-discount-popup-container').remove();
          } else {
              // This case should ideally not happen if backend consistently sends 'ok' or 'exists' for success
              alert(`Ocorreu um erro inesperado: ${data.result && data.result.message || 'Resposta desconhecida'}`); // ** <-- CORREﾃﾃグ AQUI (data.result)**
              console.error("Unexpected server response:", data);
          }
      } else {
          alert(`Ocorreu um erro: ${data.result && data.result.message || response.statusText}`); // ** <-- CORREﾃﾃグ AQUI (data.result)**
          console.error("Server error:", data);
      }
  } catch (error) {
      alert("Ocorreu um erro de rede ou servidor.");
      console.error("Fetch error:", error);
  }
};
